import React, { useState, useEffect } from "react";
import Game from "./Game";
import Timer from "./Timer";

const useStyles = makeStyles(() =>
  createStyles({
    rootContainer: {
      display: "flex", 
      alignItems: "center"
    },
    padding_container: {
      padding: 20,
    },
    height_img: {
      height: 200
    }
  }),
);

const Play = (props) => {
  const [seconds, setSeconds] = useState(0);
  const [isActive, setIsActive] = useState(false);
  const [score, setScore] = useState(0);
  const [bestScore, setBestScore] = useState(0);

  useEffect(() => {
    const bestScoreStorage = localStorage.getItem("bestScore");
    setBestScore(bestScoreStorage);
  }, [isActive]);

  return (
    <div>
      <span style={{ padding: 50 }}>Best score : {bestScore}</span>
      <br />
      <br />
      <span style={{ padding: 50 }}>Score: {score}</span>
      {isActive && <Game score={score} setScore={setScore} />}
      <br />
      <Timer
        seconds={seconds}
        setSeconds={setSeconds}
        isActive={isActive}
        setIsActive={setIsActive}
        setScore={setScore}
        score={score}
      />
    </div>
  );
};

export default Play;
